using System;using System.Collections.Generic;using System.Threading.Tasks;using TestApp2.Entities;namespace TestApp2{    public class Data{        private readonly IApi api = ApiFactory.CreateClient();        public async Task<List<Beer>> GetAllBeer(){            return await api.GetAllBeers();        }        public async Task<List<Beer>> GetBeerById(int id){            return await api.GetBeerById(id);        }    }}